package week1.day2;

public class CheckNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num=0;
		if(num>0)
		{
			System.out.println("Number is positive");
		}
		else if(num<0)
		{
			System.out.println("Number is negative");
		}
	}

}
